verification.script:
// 验证模型完整性
const ipfsData = await fetch('https://gateway.pinata.cloud/ipfs/bafkreigplhsnvzyt2z5c6llc4w6gkph6akdlyrqkr2uiiaaxdzsxfdkvl4');
const modelData = await ipfsData.arrayBuffer();
const hash = crypto.createHash('sha256').update(Buffer.from(modelData)).digest('hex');
console.assert(hash === 'cf59e4dae713d67a2f2d62e5bc653cfe0286bc460a8ea88400171e65728d555f', '模型验证失败');
